'use strict';

var fs = require("fs");

var util = {
    readConfigFile:
        function (path) {
            var data;
            
            try {
                data = fs.readFileSync(path, 'utf8');

            } catch (e) {

                console.log("CANNOT_READ_CONFIG_FILE:"+e.toString());
                return null;
            }

            return data;
        }
}

module.exports = util;